import { Component, OnInit } from '@angular/core';
import {Camera, CameraOptions} from '@ionic-native/camera/ngx';
import {Events, NavController, Platform} from '@ionic/angular';
import {StorageService} from '../../services/storage/storage.service';
import {ActivatedRoute} from '@angular/router';

@Component({
    selector: 'app-new-task',
    templateUrl: './new-task.page.html',
    styleUrls: ['./new-task.page.scss'],
})
export class NewTaskPage implements OnInit {

    image: string;
    today: string;

    data: any = {};

    constructor(
        private camera: Camera,
        private platform: Platform,
        private storageService: StorageService,
        private navCtrl: NavController,
        private route: ActivatedRoute,
        private events: Events
    ) { }

    ngOnInit() {

        this.data.selectedDate = this.formatDate();

        this.route.queryParams.subscribe(params => {

            if(params["task"]){
                this.data = params["task"];
                this.data.selectedDate = this.data.date;
            }

        });

    }

    formatDate(date = null){
        if(!date){
            date = new Date();
        }

        let year = date.getFullYear();
        let month = date.getMonth();
        month = Number(month) + 1;
        let day = date.getDate();

        return year + '-' + month + '-' + day;
    }

    openCamera(){
        if(this.platform.is('cordova')){
            const options: CameraOptions = {
                quality: 100,
                targetWidth: 700,
                targetHeight: 700,
                destinationType: this.camera.DestinationType.DATA_URL,
                encodingType: this.camera.EncodingType.JPEG,
                mediaType: this.camera.MediaType.PICTURE,
                correctOrientation: true
            };

            this.camera.getPicture(options).then(imageData => {
                this.data.image = 'data:image/jpeg;base64,' + imageData;
            });
        }
        else{
            this.data.image = 'data:image/jpeg;base64,' + '';
        }
    }

    saveNewTask(){

        this.data.date = this.formatDate(new Date(this.data.selectedDate));

        if(!this.data.title || !this.data.content || !this.data.image || !this.data.date){
            console.log('Enter all field');
            return;
        }

        if(this.data.id){
            let updatedTask = {
                id: this.data.id,
                title: this.data.title,
                content: this.data.content,
                image: this.data.image,
                date: this.data.date
            };

            this.storageService.updateTask(updatedTask);
            this.events.publish('taskEdited', updatedTask);
            this.navCtrl.pop();
        }
        else{
            this.storageService.getNextId((id) => {

                let task = {
                    id: id,
                    title: this.data.title,
                    content: this.data.content,
                    image: this.data.image,
                    date: this.data.date
                };

                this.storageService.storeNewTask(task, (state) => {
                    if(state){
                        this.events.publish('taskStored', task);
                        this.navCtrl.pop();
                    }
                });
            })
        }
    }
}
